import React, { useEffect, useState } from "react";
import NoteInput from "./components/NoteInput";
import NoteList from "./components/NoteList";
import { getNotes } from "./api";


function App() {
  const [notes, setNotes] = useState([]);

  const fetchNotes = async () => {
    const data = await getNotes();
    setNotes(data);
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleNoteCreated = (note) => setNotes([note, ...notes]);
  const handleNoteDeleted = (id) => setNotes(notes.filter((n) => n.id !== id));

  return (
    <div className="min-h-screen p-6 bg-gray-100">
      <h1 className="text-3xl font-bold text-center mb-6">
        Smart Note-Taking App
      </h1>
      <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-md">
        <NoteInput onNoteCreated={handleNoteCreated} />
        <hr className="my-6" />
        <NoteList notes={notes} onDelete={handleNoteDeleted} />
      </div>
    </div>
  );
}

export default App;
