import React from "react";
import { deleteNote } from "../api";

const NoteList = ({ notes, onDelete }) => {
  const handleDelete = async (id) => {
    await deleteNote(id);
    onDelete(id);
  };

  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">All Notes</h2>
      {notes.length === 0 && <p className="text-gray-500">No notes yet.</p>}
      {notes.map((note) => (
        <div key={note.id} className="border p-3 rounded mb-3 shadow-sm">
          <p><strong>Content:</strong> {note.content}</p>
          <p><strong>Summary:</strong> {note.summary}</p>
          <p><strong>Tags:</strong> {note.tags}</p>
          <button
            onClick={() => handleDelete(note.id)}
            className="mt-2 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default NoteList;
