import React, { useState } from "react";
import { createNote, summarizeNote } from "../api";
import NoteSummary from "./NoteSummary";

const NoteInput = ({ onNoteCreated }) => {
  const [content, setContent] = useState("");
  const [summary, setSummary] = useState("");
  const [tags, setTags] = useState("");

  const handleSummarize = async () => {
    if (!content) return;
    const result = await summarizeNote(content);
    setSummary(result); // result contains summary + optional tags
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!content) return;

    const note = await createNote({ content, summary, tags });
    onNoteCreated(note);

    setContent("");
    setSummary("");
    setTags("");
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">Create Note</h2>

      <textarea
        rows="5"
        placeholder="Write your note here..."
        value={content}
        onChange={(e) => setContent(e.target.value)}
        className="w-full p-2 border rounded mb-4 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />

      <div className="flex gap-2 mb-4">
        <button
          onClick={handleSummarize}
          className="flex-1 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Summarize
        </button>
        <button
          onClick={handleSubmit}
          className="flex-1 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Save Note
        </button>
      </div>

      <div className="bg-gray-50 p-3 rounded border">
        <NoteSummary summary={summary} tags={tags} setTags={setTags} />
      </div>
    </div>
  );
};

export default NoteInput;
