import React from "react";

const NoteSummary = ({ summary, tags, setTags }) => {
  return (
    <div className="mt-4">
      <h4 className="text-lg font-semibold mb-1">Summary:</h4>
      <textarea
        rows="3"
        value={summary}
        readOnly
        placeholder="Summary will appear here"
        className="w-full p-2 border rounded mb-3 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />
      <h4 className="text-lg font-semibold mb-1">Tags:</h4>
      <input
        type="text"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
        placeholder="Comma-separated tags"
        className="w-full p-2 border rounded mb-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />
    </div>
  );
};

export default NoteSummary;
